import React, { useState, useEffect } from 'react';

const tabs = ['Genshin', 'HSR', 'Zenless', 'Others'];

// Пример данных с дополнительными полями
const exampleData = {
  Genshin: [
    { id: '1', name: 'Sword of Dawn', price: 100, bought: 50, fromCases: 20, visible: true },
    { id: '2', name: 'Magic Wand', price: 150, bought: 30, fromCases: 15, visible: true },
  ],
  HSR: [
    { id: '3', name: 'Shield of Twilight', price: 200, bought: 40, fromCases: 10, visible: true },
  ],
  Zenless: [
    { id: '4', name: 'Zen Blade', price: 180, bought: 25, fromCases: 5, visible: true },
  ],
  Others: [
    { id: '5', name: 'Mystery Box', price: 50, bought: 100, fromCases: 50, visible: true },
  ],
};

export default function ShopPage() {
  const [activeTab, setActiveTab] = useState(tabs[0]);
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    setItems(exampleData[activeTab] || []);
    setSelectedItem(null);
  }, [activeTab]);

  // Обновление цены
  const updatePrice = (id, newPrice) => {
    setItems(prev =>
      prev.map(item => (item.id === id ? { ...item, price: newPrice } : item))
    );
  };

  // Изменение цены
  const handlePriceChange = (id, e) => {
    const val = e.target.value;
    if (/^\d*$/.test(val)) {
      updatePrice(id, val === '' ? '' : Number(val));
    }
  };

  // Обновление названия предмета
  const updateName = (id, newName) => {
    setItems(prev =>
      prev.map(item => (item.id === id ? { ...item, name: newName } : item))
    );
    if (selectedItem && selectedItem.id === id) {
      setSelectedItem(prev => ({ ...prev, name: newName }));
    }
  };

  // Изменение названия в модалке
  const handleNameChange = (e) => {
    const newName = e.target.value;
    if (selectedItem) {
      updateName(selectedItem.id, newName);
    }
  };

  // Удалить предмет
  const deleteItem = (id) => {
    if (window.confirm('Удалить предмет?')) {
      setItems(prev => prev.filter(item => item.id !== id));
      setSelectedItem(null);
    }
  };

  // Переключить видимость
  const toggleVisibility = (id) => {
    setItems(prev =>
      prev.map(item => item.id === id ? { ...item, visible: !item.visible } : item)
    );
    if (selectedItem && selectedItem.id === id) {
      setSelectedItem(prev => ({ ...prev, visible: !prev.visible }));
    }
  };

  return (
    <div>
      <h1>Магазин</h1>

      {/* Вкладки */}
      <div style={{ marginBottom: 20 }}>
        {tabs.map(tab => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              marginRight: 8,
              padding: '8px 16px',
              backgroundColor: activeTab === tab ? '#3498db' : '#ddd',
              color: activeTab === tab ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Таблица */}
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={thStyle}>ID</th>
            <th style={thStyle}>Название</th>
            <th style={thStyle}>Цена</th>
            <th style={thStyle}>Видимость</th>
            <th style={thStyle}>Управление</th>
          </tr>
        </thead>
        <tbody>
          {items.length === 0 && (
            <tr>
              <td colSpan="5" style={{ textAlign: 'center', padding: 20 }}>
                Нет предметов в этой категории.
              </td>
            </tr>
          )}
          {items.map(item => (
            <tr key={item.id} style={{ backgroundColor: item.visible ? 'inherit' : '#f8d7da' }}>
              <td style={tdStyle}>{item.id}</td>
              <td style={tdStyle}>{item.name}</td>
              <td style={tdStyle}>
                <input
                  type="text"
                  value={item.price}
                  onChange={(e) => handlePriceChange(item.id, e)}
                  style={{ width: '80px', padding: '4px' }}
                />
              </td>
              <td style={tdStyle}>{item.visible ? 'Видимый' : 'Скрыт'}</td>
              <td style={tdStyle}>
                <button style={buttonStyle} onClick={() => setSelectedItem(item)}>
                  Открыть
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Модальное окно */}
      {selectedItem && (
        <div style={modalOverlayStyle}>
          <div style={modalStyle}>
            <h2>Управление предметом</h2>

            <label>
              Название:<br />
              <input
                type="text"
                value={selectedItem.name}
                onChange={handleNameChange}
                style={{ width: '100%', padding: '6px', marginBottom: 10 }}
              />
            </label>

            <p>Куплено: {selectedItem.bought}</p>
            <p>Получено из кейсов: {selectedItem.fromCases}</p>
            <p>Видимость: {selectedItem.visible ? 'Видимый' : 'Скрыт'}</p>

            <button
              style={{ ...buttonStyle, backgroundColor: '#e74c3c', marginRight: 10 }}
              onClick={() => deleteItem(selectedItem.id)}
            >
              Удалить предмет
            </button>

            <button
              style={buttonStyle}
              onClick={() => toggleVisibility(selectedItem.id)}
            >
              {selectedItem.visible ? 'Скрыть из магазина' : 'Показать в магазине'}
            </button>

            <button style={{ marginLeft: 20 }} onClick={() => setSelectedItem(null)}>
              Закрыть
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const thStyle = {
  border: '1px solid #ccc',
  padding: '8px',
  backgroundColor: '#eee',
};

const tdStyle = {
  border: '1px solid #ccc',
  padding: '8px',
};

const buttonStyle = {
  backgroundColor: '#2980b9',
  color: '#fff',
  border: 'none',
  padding: '6px 12px',
  borderRadius: '4px',
  cursor: 'pointer',
};

const modalOverlayStyle = {
  position: 'fixed',
  top: 0, left: 0, right: 0, bottom: 0,
  backgroundColor: 'rgba(0,0,0,0.5)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  zIndex: 9999,
};

const modalStyle = {
  backgroundColor: 'white',
  padding: 20,
  borderRadius: 8,
  minWidth: 300,
};
