import React, { useState } from 'react';

const tabs = [
  { key: 'all', label: 'Все промокоды' },
  { key: 'create', label: 'Создать промокод' },
  { key: 'search', label: 'Поиск промокода' },
];

const initialPromoCodes = [
  // Пример данных
  { id: 1, code: 'WELCOME10', discount: '10%', active: true },
  { id: 2, code: 'SUMMER20', discount: '20%', active: false },
];

export default function PromoCodesPage() {
  const [activeTab, setActiveTab] = useState('all');
  const [promoCodes, setPromoCodes] = useState(initialPromoCodes);
  const [newCode, setNewCode] = useState('');
  const [newDiscount, setNewDiscount] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  // Создать промокод
  const handleCreate = () => {
    if (!newCode.trim() || !newDiscount.trim()) {
      alert('Введите код и скидку');
      return;
    }
    const exists = promoCodes.some(pc => pc.code === newCode.trim());
    if (exists) {
      alert('Такой промокод уже существует');
      return;
    }
    const newPromo = {
      id: promoCodes.length + 1,
      code: newCode.trim(),
      discount: newDiscount.trim(),
      active: true,
    };
    setPromoCodes([...promoCodes, newPromo]);
    setNewCode('');
    setNewDiscount('');
    alert('Промокод создан');
  };

  // Поиск промокодов по коду
  const handleSearch = () => {
    const results = promoCodes.filter(pc =>
      pc.code.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(results);
  };

  return (
    <div>
      <h1>Промокоды</h1>

      <div style={{ marginBottom: 20 }}>
        {tabs.map(tab => (
          <button
            key={tab.key}
            onClick={() => setActiveTab(tab.key)}
            style={{
              marginRight: 10,
              padding: '8px 16px',
              cursor: 'pointer',
              backgroundColor: activeTab === tab.key ? '#3498db' : '#ccc',
              color: activeTab === tab.key ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
            }}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {activeTab === 'all' && (
        <div>
          {promoCodes.length === 0 ? (
            <p>Промокоды отсутствуют</p>
          ) : (
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>ID</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Код</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Скидка</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Статус</th>
                </tr>
              </thead>
              <tbody>
                {promoCodes.map(pc => (
                  <tr key={pc.id}>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.id}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.code}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.discount}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>
                      {pc.active ? 'Активен' : 'Не активен'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}

      {activeTab === 'create' && (
        <div style={{ maxWidth: 400 }}>
          <div style={{ marginBottom: 10 }}>
            <label>Код промокода:</label>
            <input
              type="text"
              value={newCode}
              onChange={e => setNewCode(e.target.value)}
              style={{ width: '100%', padding: 8, boxSizing: 'border-box' }}
            />
          </div>
          <div style={{ marginBottom: 10 }}>
            <label>Скидка (например, 10%):</label>
            <input
              type="text"
              value={newDiscount}
              onChange={e => setNewDiscount(e.target.value)}
              style={{ width: '100%', padding: 8, boxSizing: 'border-box' }}
            />
          </div>
          <button onClick={handleCreate} style={{ padding: '8px 16px', cursor: 'pointer' }}>
            Создать промокод
          </button>
        </div>
      )}

      {activeTab === 'search' && (
        <div style={{ maxWidth: 600 }}>
          <div style={{ marginBottom: 10 }}>
            <input
              type="text"
              placeholder="Введите код для поиска"
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
              style={{ width: '100%', padding: 8, boxSizing: 'border-box' }}
            />
          </div>
          <button onClick={handleSearch} style={{ padding: '8px 16px', cursor: 'pointer' }}>
            Найти
          </button>

          {searchResults.length > 0 ? (
            <table style={{ marginTop: 20, width: '100%', borderCollapse: 'collapse' }}>
              <thead>
                <tr>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>ID</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Код</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Скидка</th>
                  <th style={{ border: '1px solid #ccc', padding: 8 }}>Статус</th>
                </tr>
              </thead>
              <tbody>
                {searchResults.map(pc => (
                  <tr key={pc.id}>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.id}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.code}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>{pc.discount}</td>
                    <td style={{ border: '1px solid #ccc', padding: 8 }}>
                      {pc.active ? 'Активен' : 'Не активен'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p style={{ marginTop: 20 }}>Результаты не найдены</p>
          )}
        </div>
      )}
    </div>
  );
}
