import React, { useState, useEffect } from 'react';

const tabs = ['Genshin', 'HSR', 'Zenless', 'Others'];

// Пример данных предметов (позже подтянешь с сервера)
const exampleItems = {
  Genshin: [
    { id: 1, name: 'Меч Света', price: 100, sales: 50 },
    { id: 2, name: 'Артефакт Ветра', price: 200, sales: 20 },
  ],
  HSR: [
    { id: 3, name: 'Щит Защиты', price: 150, sales: 35 },
  ],
  Zenless: [
    { id: 4, name: 'Магический посох', price: 300, sales: 10 },
  ],
  Others: [
    { id: 5, name: 'Подарочный набор', price: 50, sales: 100 },
  ],
};

export default function ShopPage() {
  const [activeTab, setActiveTab] = useState(tabs[0]);
  const [items, setItems] = useState([]);

  // Загружаем предметы при смене вкладки
  useEffect(() => {
    // Здесь сделаем fetch с сервера по категории activeTab
    // Пока подгружаем примеры
    setItems(exampleItems[activeTab] || []);
  }, [activeTab]);

  return (
    <div>
      <h1>Магазин</h1>

      {/* Вкладки */}
      <div style={{ marginBottom: 20 }}>
        {tabs.map(tab => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            style={{
              marginRight: 10,
              padding: '8px 16px',
              cursor: 'pointer',
              backgroundColor: activeTab === tab ? '#3498db' : '#ddd',
              color: activeTab === tab ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
            }}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* Таблица предметов */}
      {items.length === 0 ? (
        <p>Предметы отсутствуют</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ backgroundColor: '#eee' }}>
              <th style={thStyle}>ID</th>
              <th style={thStyle}>Название</th>
              <th style={thStyle}>Цена (USDT)</th>
              <th style={thStyle}>Продано</th>
              <th style={thStyle}>Управление</th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => (
              <tr key={item.id} style={{ borderBottom: '1px solid #ccc' }}>
                <td style={tdStyle}>{item.id}</td>
                <td style={tdStyle}>{item.name}</td>
                <td style={tdStyle}>{item.price}</td>
                <td style={tdStyle}>{item.sales}</td>
                <td style={tdStyle}>
                  <button
                    style={buttonStyle}
                    onClick={() => alert(`Управление предметом "${item.name}" (заглушка)`)}
                  >
                    Управление
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const thStyle = {
  padding: '10px',
  borderBottom: '2px solid #bbb',
  textAlign: 'left',
};

const tdStyle = {
  padding: '10px',
};

const buttonStyle = {
  backgroundColor: '#2980b9',
  color: '#fff',
  border: 'none',
  padding: '6px 12px',
  borderRadius: '4px',
  cursor: 'pointer',
};
