import React, { useState, useEffect } from 'react';

export default function ItemsPage() {
  // Пример начальных данных - потом их можно подгружать с API
  const [items, setItems] = useState([
    { id: 1, name: 'Sword of Dawn', price: 100 },
    { id: 2, name: 'Shield of Twilight', price: 150 },
    { id: 3, name: 'Helmet of Storms', price: 80 },
  ]);

  // Обновление цены для предмета
  const updatePrice = (id, newPrice) => {
    setItems(prevItems =>
      prevItems.map(item =>
        item.id === id ? { ...item, price: newPrice } : item
      )
    );
  };

  // Функция для обработки изменения input-а цены
  const handlePriceChange = (id, event) => {
    const value = event.target.value;
    if (/^\d*$/.test(value)) { // только цифры
      updatePrice(id, value === '' ? '' : Number(value));
    }
  };

  return (
    <div>
      <h1>Страница предметов</h1>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>ID</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Название</th>
            <th style={{ border: '1px solid #ccc', padding: 8 }}>Цена</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{item.id}</td>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>{item.name}</td>
              <td style={{ border: '1px solid #ccc', padding: 8 }}>
                <input
                  type="text"
                  value={item.price}
                  onChange={(e) => handlePriceChange(item.id, e)}
                  style={{ width: '80px', padding: '4px' }}
                />
              </td>
            </tr>
          ))}
          {items.length === 0 && (
            <tr>
              <td colSpan="3" style={{ textAlign: 'center', padding: 10 }}>
                Нет предметов для отображения.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
